AND p.update_Date >= #{beginTime}
AND p.update_Date <= #{endTime}
GROUP BY u.id ORDER BY u.id ) t2 ON t1.id = t2.id )yy ON ww.id = yy.id LEFT JOIN (SELECT u.id,u.role_id,u.company_id,u.user_name,u.group_level,IFNULL(sum(d.real_money),0) AS '实收金额',IFNULL(sum(d.real_principle),0) FROM back_user u LEFT JOIN credit_loan_pay_detail d ON u.uuid = d.current_collection_user_id WHERE u.`status` = 1 AND u.role_id = '10021'
AND d.update_Date >= #{beginTime}
AND d.update_Date <= #{endTime}
GROUP BY u.uuid) hk ON ww.id = hk.id GROUP BY ww.company_id,ww.group_level ;

mapper.delete:
DELETE ps FROM person_statistics ps
WHERE  ps.createDate >= #{beginTime} 
 AND ps.createDate <= #{endTime}

DELETE cs FROM company_statistics cs
WHERE  cs.createDate >= #{beginTime} 
 AND cs.createDate <= #{endTime}
