mapper.select:
SELECT id AS id, batch_id AS batchId, merchant_no AS merchantNo, user_id AS userId, user_name AS userName, mobile AS mobile, register_time AS registerTime, dispatcher_time AS dispatcherTime, loan_order_status AS loanOrderStatus, user_intention AS userIntention, remark AS remark, current_collection_user_id AS currentCollectionUserId, current_collection_user_name AS currentCollectionUserName, company_id AS companyId, create_time AS createTime, update_time AS updatetime FROM xiao_shou_order where 1=1
and merchant_no = #{merchantNo}
and batch_id = #{batchId}
and user_intention = #{userIntention}
and company_id = #{companyId}
and user_name = #{userName}
and current_collection_user_id = #{currentCollectionUserId}
and dispatcher_time >= #{startDispatcherTime}
and dispatcher_time <= #{endDispatcherTime}
ORDER BY dispatcher_time DESC, register_time DESC

SELECT
count(*)
FROM
xiao_shou_order
where 1=1

	and merchant_no = #{merchantNo}


	and batch_id = #{batchId}


	and user_intention = #{userIntention}


	and company_id = #{companyId}


	and user_name = #{userName}


	and current_collection_user_id = #{currentCollectionUserId}


	and dispatcher_time >= #{startDispatcherTime}


	and dispatcher_time <= #{endDispatcherTime}
